import React, { Component } from 'react';
import './App.css';
import Web3 from 'web3';

// Three components: 
// 1: BlockBox for displaying block with relative size
// 2: BlockDetails for displaying transactions with msg.value > 0
// 3: TransactionDetails for displaying details about transaction

import BlockBox from '../components/BlockBox/BlockBox';
import BlockDetails from '../components/BlockDetails/BlockDetails';


const web3 = new Web3(Web3.givenProvider);

class App extends Component {

  state = {
    blocks: [],
    activeBlock: null,
    web3Connected: null
  }

  getBlocks = () => {
    let blocks = [];
    web3.eth.getBlockNumber()
      .then(latestBlockNumber => {
        for (let i = 0; i < 10; i++) {
          web3.eth.getBlock(latestBlockNumber - i)
            .then(block => {
              blocks.push({
                blockNumber: block.number,
                transactions: block.transactions,
                transactionNumber: block.transactions.length
              })
              this.setState({ blocks });
            })
            .catch(err => {
              console.log(err);
            });
        }
      })
      .then(blocks => {
        this.setState({ blocks });
      })
      .catch(err => {
        console.log(err);
      });
  }

  selectBlock = (blockNumber) => {
    // get selected/active block
    const activeBlock = this.state.blocks.filter(block => {
      return block.blockNumber === blockNumber;
    })

    const filteredTransactions = [];

    activeBlock[0].transactions.forEach(txHash => {
      web3.eth.getTransaction(txHash)
        .then(tx => {
          if (tx.value > 0) {
            filteredTransactions.push(tx);
          }
          this.setState({ activeBlockTx: filteredTransactions });
        })
    });
    this.setState({
      activeBlock: activeBlock[0].blockNumber,
    });
  }

  componentDidMount() {
    // Identify which network the app is connected to:
    this.getNetwork()
    // get the blocks
    this.getBlocks();
  }

  getNetwork = () => {
    let networkName = '';
    web3 && web3.currentProvider.publicConfigStore.on('update', () => {
      web3.eth.net.getId()
        .then(res => {
          console.log(res);
          switch (res) {
            case "1":
              networkName = "Ethereum Mainnet";
              break;
            case "2":
              networkName = "Morden Testnet";
              break;
            case "3":
              networkName = "Ropsten Testnet";
              break;
            case "4":
              networkName = "Rinkeby Testnet";
              break;
            case "42":
              networkName = "Kovan Testnet";
              break;
            default:
              networkName = "Unknown";
          }
          this.setState({
            web3Connected: networkName,
          })
        }
        )
        .catch(err => {
          console.log(err);
        })
    });
  }

  componentDidUpdate() {
  }

  render() {
    if (!web3) {
      return (
        <main style={{ backgroundColor: 'white', display: 'flex', flexFlow: 'column wrap', height: '100vh' }}>
          <div style={{ background: 'white', display: 'flex', justifyContent: 'center' }}>
            <h1>⚠️</h1>
          </div>
          <div style={{ backgroundColor: 'white', paddingLeft: '25%', paddingRight: '25%', display: 'flex', justifyContent: 'center' }}>
            <p>
            <center>No Ethereum connection found!</center>
            <br /><br />
            <center>Please install <a href="https://metamask.io/" target="_blank" rel="noopener noreferrer">Metamask</a> or use the <a href="https://brave.com/download/" target="_blank" rel="noopener noreferrer">Brave</a> browser 
            with Metamask installed.</center>
            <br /><br />
            <center>Make sure you're connected to Mainnet or the Rinkeby test network.</center>
            </p>
          </div>
        </main>
      )
    }
    let blocks;
    let totalTransactions = 0;

    // only render individual block components, once all blocks have been retrieved
    if (this.state.blocks && this.state.blocks.length === 10) {
      // get the number of total transactions across all 10 blocks in order to display relative block sizes
      for (let i = 0; i < 10; i++) {
        totalTransactions += this.state.blocks[i].transactionNumber;
      }
      blocks = this.state.blocks && this.state.blocks.map(block => {
        return (
          // calculate the width of the box based on the relative transaction volume 
          // (percentage of #transactions in block relative to total #transactions across the 10 blocks)
          <BlockBox
            key={block.blockNumber}
            blockData={block}
            width={Math.floor((block.transactionNumber / totalTransactions) * 100)}
            selectBlock={this.selectBlock}
            active={block.blockNumber === this.state.activeBlock}
          />

        );
      })
    }

    const connectionStatus = this.state.web3Connected
      ? (<p><b>Connected</b> <br /> {this.state.web3Connected}</p>)
      : (<p><b> No Web3</b> <br /> Activate Metamask </p>);


    return (
      <div className="App">
        <header className="App-header">
          <div className="network">
            {/* Show connection status */}
            {connectionStatus}
          </div>
          <p>
            LAST 10 ETHEREUM BLOCKS
          </p>
          <button onClick={this.getBlocks}>Refresh</button>
        </header>
        <div className="Blocks">
          {blocks}
        </div>
        <div>
          <BlockDetails
            blockNumber={this.state.activeBlock}
            txDetails={this.state.activeBlockTx} />
        </div>
      </div>
    );
  }
}

export default App;
