import React from 'react';
import './BlockDetails.css';

const BlockDetails = props => {
    console.log(props.txDetails)

    let rows;
    if (!props.blockNumber) {
       rows = <h1>Select block to see transactions</h1>;
    } else {
        rows = props.txDetails && props.txDetails.map(tx => {
            return (
                <tr>
                    <td>{tx.from}</td>
                    <td>{tx.to}</td>
                    <td>{(tx.value/10**18).toFixed(5)}</td>
                    <td><a href={`https://etherscan.io/tx/${tx.hash}`}>{tx.hash.substring(0,25) + "..."}</a></td>
                </tr>
            );
        });
    }


    return (
        <div className="Details">
        <h1> Block {props.blockNumber}</h1>
        <table>
            <th>from</th>
            <th>to</th>
            <th>value (eth)</th>
            <th>etherscan</th>
            {rows}
        </table>
        </div>
    );
}

export default BlockDetails;
