import React, { Component } from 'react';
import './App.css';
import Web3 from 'web3';

const web3 = new Web3(Web3.givenProvider || "https://mainnet.infura.io/v3/f95b0011215b4ae494c029e3da037c24");
web3.eth.getAccounts().then(console.log);

class App extends Component {

  getBlocks = () => {
    web3.eth.getBlockNumber()
      .then(latestBlockNumber => {
        for (var i = 0; i < 10; i++) {
          web3.eth.getBlock(latestBlockNumber - i)
            .then(block=> {
              this.setState({i: block.transactions})
            })
            .catch(err => {
              console.log(err);
            });
        }
      })
      .catch(err => {
        console.log(err);
      });
  }

  componentDidMount() {
    this.getBlocks();
  }

  componentDidUpdate() {
    console.log(this.state);
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <p>
            The 10 latest blocks on the Ethereum mainnet
          </p>
        </header>
        <button onClick={this.getBlocks}>Refresh</button>
      </div>
    );
  }
}

export default App;
