import React from 'react';
import './BlockDetails.css';

const BlockDetails = props => {
    let title;
    let rows;
    let table;
    if (!props.blockNumber) {
        title = <h3>Select block to see transactions with value > 0</h3>;
    } else {
        title = <h3> Block {props.blockNumber}</h3>
        table = (
            <table style={{ margin: 'auto' }}>
                <tbody>
                    <tr>
                        <th>from</th>
                        <th>to</th>
                        <th>value (eth)</th>
                        <th>etherscan</th>
                    </tr>
                    {rows}
                </tbody>
            </table>
        )
        rows = props.txDetails && props.txDetails.map(tx => {
            return (
                <tr key={tx.hash}>
                    <td>{tx.from}</td>
                    <td>{tx.to}</td>
                    <td>{(tx.value / 10 ** 18).toFixed(5)}</td>
                    <td><a href={`https://etherscan.io/tx/${tx.hash}`}>{tx.hash.substring(0, 25) + "..."}</a></td>
                </tr>
            );
        });
    }

    return (
        <div className="Details">
            {title}
            {table}
        </div>
    );
}

export default BlockDetails;
