import React from 'react';
import './BlockDetails.css';

const BlockDetails = props => {

    let message;
    if (!props.block) {
       message = <h1>Select block to see transactions</h1>;
    }

    return (
        <div>
            {message}
            {props.block}
        </div>
    );
}

export default BlockDetails;
