import React from 'react';
import './BlockDetails.css';

const BlockDetails = props => {

    let box;
    if (!props.block) {
       box = <h1>Select block to see transactions</h1>;
    } else {
        box = props.block.transactions;
    }

    return (
        <div>
            {box}
        </div>
    );
}

export default BlockDetails;
