import React from 'react';
import './BlockBox.css';

const BlockBox = props => {
    const width = Math.floor((props.blockData.transactions.length / props.totalTransactions) * 100);

    return (
        <div className="BlockBox" style={{ width: width, order: -props.blockData.blockNumber }}>
            <p className="BlockNumber">
                #{props.blockData.blockNumber}
            </p>
        </div>
    );
}

export default BlockBox;
