import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './containers/App';
import * as serviceWorker from './serviceWorker';
import NotConnected from './components/NotConnected/NotConnected';

// This app consists of 1 stateful components and 2 presentational components: 
//
// containers/App.js (stateful): Connects to the Ethereum blockchain and fetches data for last 10 blocks.
//
// components/BlockBox (stateless): Shows the last 10 blocks on a "timeline" for the user to select one.
// The blocks are sized to visually represent the size of the corresponding block (total number of transactions) 
//
// components/BlockDetails: Lists all transactions from the selected block
// which have value > 0 (i.e. Ether was sent with these transactions).
//


ReactDOM.render(<App/>, document.getElementById('root'));


serviceWorker.unregister();
